ColumnSpecification.Data:
if (alignment.type ~= "mainTrack" or alignment.mainDir == "down") and (dir == "up" or dir == "both") then 
	return getInvertedBlockImage(false,false,true,3)
end

if (alignment.type ~= "mainTrack" or alignment.mainDir == "down") and (dir == "down") then  
	return getInvertedBlockImage(false,false,true,3)
end

if (alignment.type == "mainTrack" and alignment.mainDir == "up") and (dir == "up" or dir == "both") then  
	return getInvertedBlockImage(false,false,true,3)
end

if (alignment.type == "mainTrack" and alignment.mainDir == "up") and (dir == "down") then  
	return getInvertedBlockImage(false,false,true,3)
end

info = getAlignmentInfo()
if info.Segment.Type ~= "Curve" then return nil end
return info.CurveRadius == math.huge and info.CurveRadius or string.format("%.3f", info.CurveRadius)

if Variant ~= "OB" then return nil end
info = getAlignmentInfo()
return info and info.NearestHorizontalVertex and string.format("%.3f", info.NearestHorizontalVertex.Item2.Length)

LuaFunction.Formula:
function _KU_KFOE_Duct_Rotation3dZ(AlignmentRefAndPos)
	local s,e,m
	s,e = _KU_KFOE_Duct_getStartEndAndJump(AlignmentRefAndPos)
	m = getAlignmentInfo(AlignmentRefAndPos)
	return (math.atan(e.Point.Y-s.Point.Y,e.Point.X-s.Point.X) - math.atan(m.Tangent.Y,m.Tangent.X)) * (180/math.pi)
end

function _KU_KFOE_Package_Linetype()
	return "Continuous" --_info("Variant ["..Variant.."] has not been anticipated yet.")
end

function _KU_KFOE_Package_GlobalWidth()
	return 1.0 --_info("Variant ["..Variant.."] has not been anticipated yet.")
end

function _KU_KFOE_Package_Model3DName()
	return "",_info("Variant ["..Variant.."] has not been anticipated yet.")
end

