LuaFunction.Formula:
function NOBN_sig_chkTriggerNearbySpeedBoards()
	local tol = 1.0 --[m]
	local speedBoards = getNearbyPointObjects2D(tol,RcType)
	local n = getCollectionLength(speedBoards)
	return n,speedBoards, _info(n.." speed boards have been refreshed.")
end

