DefaultInterlockingExportOptions.TrainRoutesStartPointFilter:
if (RcType == "JBTSA_SIG Signal" and Hovedsignal == 'Hs2' or Hovedsignal == 'Hs3') then
	return NOBN_sig_getSignalShortName()
elseif RcType == "JBTSA_ERT ERTMS-signal" then
	return NOBN_sig_getSignalShortName()
end

DefaultInterlockingExportOptions.TrainRoutesEndPointFilter:
if ((RcType == "JBTSA_SIG Signal" and (Hovedsignal == 'Hs1'or Hovedsignal == 'Hs2' or Hovedsignal == 'Hs3'))
	or (RcType == "JBTSA_FIK Fiktivt punkt" and Variant == "Fiktivt sluttpunkt for togvei")
	or (RcType == "JBTSA_SIG Togvei slutt"))
then
	return NOBN_sig_getSignalShortName()
elseif RcType == "JBTSA_ERT ERTMS-signal" then
	return NOBN_sig_getSignalShortName()
end

DefaultInterlockingExportOptions.ShuntingRoutesStartPointFilter:
if (RcType == "JBTSA_SIG Signal" and Dvergsignal == "Ja") then
	return NOBN_sig_getSignalNumber()
end
