function _JBTEH_MAS_non_yoke_mast_code()
	entireKm = math.floor(ReferenceMileage/1000)
	return string.format("%d-%02d",entireKm,seq), _info("Mast's code is inherited from reference mileage ("..ReferenceMileage..") and the local variable seq ("..seq..").")
end

--[[ _JBTEH_MAS_Spanlength.lua 2022-10-09_003 CLFEY Re-written to fit DNA NO-BN 2022.a where wtb and cnt are chained. Also handles wtb mounted on a tunnel wall baseplate. uses new system with global lua names for RcType and relation forward / reverse names. Revised method: - Start at OCS pole and find its cantilevers and wtb's, or start at or tunnel wall baseplate and find its wtb. - Iterate over all such objects found, locate the next cantilever or wtb in sequence - For each next object, store next object's contact wire name and the straight-line 2D distance in the XY plane from CW wireclamp/wtb to CW wireclamp/wtb. Return value: A collection of collections, each containing the 'name' of a contact wire and the straight-line 2D distance to next item. --]] function _JBTEH_MAS_Spanlength() local tol = 1.0 --[m] Tolerance when searching for wire tension balancers at an OCS pole local result = {}
--Find all cantilevers and WTBs at this OCS pole, but skip WTBs serving midpoint anchor lines or AT conductors, earthing wires etc: local rCnt = getRelatedObjects(rel_OcsPole_Has_Cantilever) local rWtb = getNearbyPointObjects2D(tol,rctype_WireTensioner) :filter(function (x) return x.Alignment == rctype_ContactWire end) local items = getUnionOfCollections({rCnt,rWtb}) local nItems = getCollectionLength(items) local i
if nItems == 0 then --Poles for supporting OCS portals will exist, with neither wtbs nor cantilevers at the pole. return {{"No spanlengths",0}},info("No spanlengths") else for i = 0, nItems-1 do local thisItem = items[i] local cowName local p1 if thisItem.RcType == rctype_WireTensioner then p1 = thisItem:getPoint3D() cowName = thisItem.Alignment.name else --thisItem.RcType == rctype_Cantilever: p1 = thisItem:NOBN_ocs_getWireClampPoint3D() cowName = thisItem:getRelatedObjects(rel_Cantilever_Holds_ContactWire)[0].name end local nextItems,nNextItems = thisItem:getRelatedObjects(rel_CantileverOrWtb_HasNext_CantileverOrWtb) --A WTB might be the terminal for a contact wire (at a buffer stop etc). Treat only those who possess a successor: if nNextItems > 0 then nextItem = nextItems[0] if nextItem.RcType == rctype_WireTensioner then p2 = nextItem:getPoint3D() else --nextItem.RcType == rctype_Cantilever: p2 = nextItem:NOBN_ocs_getWireClampPoint3D() end distance2D = RC__getDistance2D(p1,p2) table.insert(result,{cowName,RC__round(distance2D,1)}) end end end return result,_info(getCollectionLength(result).." spanlength(s)") end

function _JBTEH_MAS_RelativeElevation()
	return 0
end

function _JBTEH_MAS_mast_AngularOffset() local yRelationName = rel_OcsPole_IsSupportFor_OcsPortal yr,yn = getRelatedObjects(yRelationName) if yn == 0 then return getPropertyValue("AngularOffset"), _info("No control input - adjust angular offset manually or relate with '"..yRelationName.."' to a yoke (which has second support pole).")
else local yoke = yr[0] local s = rel_OcsPortal_IsSupportedBy_OcsPole local r,n = getRelatedObjects(s,yoke)
if n == 0 then return getPropertyValue("AngularOffset"),_error, _info("ERROR OCS pole has bad connection to galley '"..RC__identify(yoke).."'.")
elseif n == 1 then --Galley: return getPropertyValue("AngularOffset"), _info("OCS pole is connected to a galley '"..RC__identify(yoke).. "' - adjust pole's angular offset manually or change galley into yoke (which has one more yoke support poles).")
elseif n == 2 then --Assume that the first two related poles are located at the yoke's extremities: local m1 = r[0] local m2 = r[1] if RC__getDistance2D(this,m1) > RC__getDistance2D(this,m2) then m1,m2 = m2,m1 end local x1 = m1.geoCoord.X local y1 = m1.geoCoord.Y local x2 = m2.geoCoord.X local y2 = m2.geoCoord.Y local yokeAngle = math.atan(y2-y1,x2-x1) * (180/math.pi) local tg = getAlignmentInfo().Tangent local baseAngle = math.atan(tg.Y,tg.X) * (180/math.pi) local info = "Angular Offset deduced using yoke support poles '"..RC__identify(m1).."' and '"..RC__identify(m2).."'." return (2*360.0 + RC__round(yokeAngle - (baseAngle-90.0),3)) % 360.0, _info(info)
else --TODO: Locate the two support poles at each end of yoke, use this to find angular offset, then turn yourself if you --are a support pole at one or the other end, or just adapt if you are in between ends. return getPropertyValue("AngularOffset"), _info("OCS pole is connected to a yoke '"..RC__identify(yoke).." with total 3 or more supporting masts".. "' - adjust pole's angular offset manually.") end end end

