LuaFunction.Formula:
function _JBTEH_AAK_AngularOffset() local RelationName = rel_OcsPortal_IsSupportedBy_OcsPole local r, n = getRelatedObjects(RelationName)
if n == 0 then return getPropertyValue("AngularOffset"), _info("No control input - adjust angular offset manually. Relate to one (gantry) or two (portal) poles using '"..RelationName.."' to set angular offset automatically.")
elseif n == 1 then -- Gantry "Ã¥k-unge", "utliggerÃ¥k" -- Assume gantry belongs to same alignment as the supporting OCS pole return r[0].AngularOffset, _info("AngularOffset for gantry deduced from object '"..RC__identify(r[0]).."' related by '"..RelationName.."'.")
elseif n == 2 then --Assume that the first two related OCS poles are located at the portal's extremities: local m1 = r[0] local m2 = r[1] local x1 = m1.geoCoord.X local y1 = m1.geoCoord.Y local x2 = m2.geoCoord.X local y2 = m2.geoCoord.Y local portalDir = math.deg(math.atan(y2-y1, x2-x1)) local tg = getAlignmentInfo().Tangent local trackDir = math.deg(math.atan(tg.Y, tg.X)) - RC__getAngleFromDir() local info = "AngularOffset for portal deduced from its support poles '".. RC__identify(m1).."' and '"..RC__identify(m2).."' related by '"..RelationName.."'." return RC__round((2*360 + portalDir - trackDir) % 360, 3), _info(info)
else --TODO: Locate the two support poles at each end of portal, uses thes to find angular offset, then turn yourself if you --are a support pole at one or the other end, or just adapt if you are in between ends. return getPropertyValue("AngularOffset"), _info("Mast is connected to a portal '"..RC__identify(portal).." with total 3 or more supporting poles".. "' - adjust pole's angular offset manually.") end end

function _JBTEH_AAK_Mileage() local s = rel_OcsPortal_IsSupportedBy_OcsPole local r, n = getRelatedObjects(s) if n == 0 then return getPropertyValue("Mileage"), _info("No control input - adjust mileage manually. Relate portal to one or two poles using '"..s.."' to set mileage automatically.")
elseif n == 1 then -- Move to related mast (assuming gantry (UtliggerÃ¥k)) return RC__getMileageFromRelatedObject(s), _info("Mileage deduced from support pole '"..RC__identify(r[0]).."'.")
elseif n >= 2 then local m1 = r[0] local m2 = r[1] if RC__getDistance2D(this, m1) > RC__getDistance2D(this, m2) then m1, m2 = m2, m1 end local d1 = RC__getDistance2D(m1, m2) local L = math.floor(1.500 + d1) --Assume min 0.5 and max 1.5 meter total portal length overshoot local d2 = 0.5 * (L - d1) --Share overshoot between poles local x1 = m1.geoCoord.X local y1 = m1.geoCoord.Y local x2 = m2.geoCoord.X local y2 = m2.geoCoord.Y local x = x1 - (d2/d1)*(x2-x1) local y = y1 - (d2/d1)*(y2-y1) local info = "Mileage deduced through support pole '"..RC__identify(m1).."' related by '"..s.."'." return getAlignmentInfo(Alignment.id, x, y).Mileage, _info(info) end end

function _JBTEH_AAK_Geometry3DName()
	if Variant == "Type 12" then 
		return string.upper(string.format("NO-BN-3D-EH-AAK-KONTAKTLEDNINGSÃK-TYPE-12-%02d-%s",Length:sub(1,-2)-10, Length))
	elseif Variant == "Type 14" then 
		return string.upper(string.format("NO-BN-3D-EH-AAK-KONTAKTLEDNINGSÃK-TYPE-14-%02d-%s",Length:sub(1,-2)-27, Length))
	elseif Variant == "Type 40" then 
		return string.upper(string.format("NO-BN-3D-EH-AAK-KONTAKTLEDNINGSÃK-TYPE-40-%02d-%s",Length:sub(1,-2)-9, Length))
	elseif Variant == "OCS gantry" then
		return "NO-BN-3D-EH-AAK-UTLIGGERÃK-6M"
	else
		return "Bad OCS portal Variant ["..Variant.."]",_warning
	end
end

