function JBTEH_MAS_nonPortalPole_code()
	entireKm = math.floor(ReferenceMileage/1000)
	return string.format("%d-%02d",entireKm,seq), _info("Pole's code is inherited from reference mileage ("..ReferenceMileage..") and the local variable seq ("..seq..").")
end

function _JBTEH_MAS_Spanlength() --[[ This function assumes that wtb and cnt are chained with a common previous/next relation. Also handles wtb mounted on a tunnel wall baseplate. - Start at OCS pole and find its cantilevers and wtb's, or start at or tunnel wall baseplate and find its wtb. - Iterate over all such objects found, locate the next cantilever or wtb in sequence - For each next object, store next object's contact wire name and the straight-line 2D distance in the XY plane from CW wireclamp/wtb to CW wireclamp/wtb. Return value: A collection of collections, each containing the 'name' of a contact wire and the straight-line 2D distance to next item. --]] local tol = 1.0 --[m] Tolerance when searching for wire tension balancers at an OCS pole local result = {}
--Find all cantilevers and WTBs at this OCS pole, but skip WTBs serving midpoint anchor lines or AT conductors, earthing wires etc: local rCnt = getRelatedObjects(rel_OcsPole_Has_Cantilever) local rWtb = getNearbyPointObjects2D(tol,rctype_WireTensioner) :filter(function (x) return x.Alignment == rctype_ContactWire end) local items = getUnionOfCollections({rCnt,rWtb}) local nItems = getCollectionLength(items) local i
if nItems == 0 then --Poles for supporting OCS portals will exist, with neither wtbs nor cantilevers at the pole. return {{"No spanlengths",0}}, _info("No spanlengths") else for i = 0, nItems-1 do local thisItem = items[i] local cowName local p1 if thisItem.RcType == rctype_WireTensioner then p1 = thisItem:getPoint3D() cowName = thisItem.Alignment.name else --thisItem.RcType == rctype_Cantilever: p1 = thisItem:NOBN_ocs_getWireClampPoint3D() cowName = thisItem:getRelatedObjects(rel_Cantilever_Holds_ContactWire)[0].name end local nextItems,nNextItems = thisItem:getRelatedObjects(rel_CantileverOrWtb_HasNext_CantileverOrWtb) --A WTB might be the terminal for a contact wire (at a buffer stop etc). Treat only those who possess a successor: if nNextItems > 0 then nextItem = nextItems[0] if nextItem.RcType == rctype_WireTensioner then p2 = nextItem:getPoint3D() else --nextItem.RcType == rctype_Cantilever: p2 = nextItem:NOBN_ocs_getWireClampPoint3D() end distance2D = RC__getDistance2D(p1,p2) table.insert(result,{cowName,RC__round(distance2D,1)}) end end end return result, _info(getCollectionLength(result).." spanlength(s)") end

function _JBTEH_MAS_RelativeElevation()
	return 0
end

function _JBTEH_MAS_ocsPole_AngularOffset() local pRelationName = rel_OcsPole_IsSupportFor_OcsPortal pr,pn = getRelatedObjects(pRelationName) if pn == 0 then return getPropertyValue("AngularOffset"), _info("No control input - adjust angular offset manually or relate with '"..pRelationName.."' to a portal (which has second support pole).")
else local portal = pr[0] local s = rel_OcsPortal_IsSupportedBy_OcsPole local r,n = getRelatedObjects(s,portal)
if n == 0 then return getPropertyValue("AngularOffset"),_error, _info("ERROR OCS pole has bad connection to gantry '"..RC__identify(portal).."'.")
elseif n == 1 then --Gantry: return getPropertyValue("AngularOffset"), _info("OCS pole is connected to a gantry '"..RC__identify(portal).. "' - adjust pole's angular offset manually or change gantry into portal (which has one more portal support poles).")
elseif n == 2 then --Assume that the first two related poles are located at the portal's extremities: local m1 = r[0] local m2 = r[1] local x1 = m1.geoCoord.X local y1 = m1.geoCoord.Y local x2 = m2.geoCoord.X local y2 = m2.geoCoord.Y local portalDir = math.deg(math.atan(y2-y1,x2-x1)) % 180.0 local tg = getAlignmentInfo().Tangent local trackDir = math.deg(math.atan(tg.Y,tg.X)) local info = "Angular Offset deduced using portal support poles '"..RC__identify(m1).."' and '"..RC__identify(m2).."'." return (360.0 + RC__round(90 - (trackDir - portalDir),3)) % 360.0, _info(info)
else --TODO: Locate the two support poles at each end of portal, use this to find angular offset, then turn yourself if you --are a support pole at one or the other end, or just adapt if you are in between ends. return getPropertyValue("AngularOffset"), _info("OCS pole is connected to a portal '"..RC__identify(portal).." with total 3 or more supporting poles".. "' - adjust pole's angular offset manually.") end end end

