LuaFunction.Formula:
function NOBN_com_FDVBaneDataBeskrivelse()
	return BaneNORBeskrivelse, _info("Verdi er hentet fra [Pset Bane NOR].Beskrivelse")
end

function NOBN_com_Pset_FDVBaneDataSportypeFra() if RcAlignment and RcType == "JBTKO_SPO Spor" then --Linjeobjekt av type jernbanespor: --(Use getPropertyValue() to avoid an endless loop, since also tracks will be assigned this formula) return getPropertyValue("FDVBaneDataSporTypeFra")
elseif Alignment and Alignment.RcType == "JBTKO_SPO Spor" then --Punktobjekt i jernbanespor: return Alignment.FDVBaneDataSporTypeFra
else --Ingen direkte angivelse av sportilhørighet: if RcAlignment then --Linjetype av annen type enn jernbanespor: x = RcAlignment.StartPoint.X y = RcAlignment.StartPoint.Y else --Punktobjekt i annen linjetype enn jernbanespor: x = geoCoord.X y = geoCoord.Y end t = getClosestTracks(x,y) n = getCollectionLength(t) --Exploit the fact that closest tracks are sorted by proximity: for i = 0,n-1 do ai = getAlignmentInfo(t[i].id,x,y) if ai.NormalProjectionExists then return t[i].FDVBaneDataSporTypeFra end end --Fant ingen spor vi kunne projisere oss inn på: if RcAlignment then return _warning, "Ukjent", _info("Linjens startpunkt har ikke en projeksjon på noen av sporene i modellen, kan derfor ikke utlede tilhørende spors anvendelsestype.") else return _warning, "Ukjent", _info("Punktobjektet har ikke en projeksjon på noen av sporene i modellen, kan derfor ikke utlede tilhørende spors anvendelsestype.") end end end

function NOBN_com_Pset_FDVBaneDataSporNrFra() if RcAlignment and RcType == "JBTKO_SPO Spor" then --Linjeobjekt av type jernbanespor: return RC__identify(this)
elseif Alignment and Alignment.RcType == "JBTKO_SPO Spor" then --Punktobjekt i jernbanespor: return RC__identify(Alignment)
else --Ingen direkte angivelse av sportilhørighet: if RcAlignment then --Linjetype av annen type enn jernbanespor: x = RcAlignment.StartPoint.X y = RcAlignment.StartPoint.Y else --Punktobjekt i annen linjetype enn jernbanespor: x = geoCoord.X y = geoCoord.Y end t = getClosestTracks(x,y) n = getCollectionLength(t) --Exploit the fact that closest tracks are sorted by proximity: for i = 0,n-1 do ai = getAlignmentInfo(t[i].id,x,y) if ai.NormalProjectionExists then return RC__identify(t[i]) end end --Fant ingen spor vi kunne projisere oss inn på: if RcAlignment then return _warning, "Linjens startpunkt har ikke en projeksjon på noen av sporene i modellen, kan derfor ikke utlede sportilhørighet." else return _warning, "Punktobjektet har ikke en projeksjon på noen av sporene i modellen, kan derfor ikke utlede sportilhørighet." end end end

function FDVBaneDataAvstSpormidtFra() if RcAlignment and RcType == "JBTKO_SPO Spor" then --Linjeobjekt av type jernbanespor: return 0,info("Null avstand til seg selv, spor "..RC__identify(this))
elseif Alignment and Alignment.RcType == "JBTKO_SPO Spor" then --Punktobjekt i jernbanespor: return RC__round(DistanceToAlignment,3) ,info("Avstand til spor "..RC__identify(Alignment))
else --Ingen direkte angivelse av sportilhørighet: if RcAlignment then --Linjetype av annen type enn jernbanespor: x = RcAlignment.StartPoint.X y = RcAlignment.StartPoint.Y else --Punktobjekt tilhørende en annen linjetype enn jernbanespor: x = geoCoord.X y = geoCoord.Y end t = getClosestTracks(x,y) n = getCollectionLength(t) --Exploit the fact that closest tracks are sorted by proximity: for i = 0,n-1 do ai = getAlignmentInfo(t[i].id,x,y) if ai.NormalProjectionExists then return ai.DistanceToAlignment, _info("Avstand til spor "..RC__identify(t[i])) end end --Fant ingen spor vi kunne projisere oss inn på: if RcAlignment then return _error, 0, _info("Linjens startpunkt har ikke en projeksjon på noen av sporene i modellen, kan derfor ikke utlede avstand sideveis til noe spor.") else return _error, 0, _info("Punktobjektet har ikke en projeksjon på noen av sporene i modellen, kan derfor ikke utlede avstand sideveis til noe spor.") end end end

function NOBN_com_FDVBaneDataNordFra()
	if RcAlignment then
		--Alignment object:
		return RC__round(RcAlignment.StartPoint.Y,3)
	else
		--Point object:
		return string.format("%.3f",geoCoord.Y)
	end
end

function NOBN_com_FDVBaneDataOestFra()
	if RcAlignment then
		--Alignment object:
		return RC__round(RcAlignment.StartPoint.X,3)
	else
		--Point object:
		return string.format("%.3f",geoCoord.X)
	end
end

function NOBN_com_FDVBaneDataHoeydeFra()
	if RcAlignment then
		--Alignment object:
		return RC__round(RcAlignment.StartPoint.Z,3)
	else
		--Point object:
		return string.format("%.3f",geoCoord.Z)
	end
end
